import React from 'react';
import { Link } from 'react-router-dom'; // Import Link
import { Carousel } from "react-responsive-carousel"; // Import Carousel
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Import Carousel styles
import './Gallery.css'; // Import your CSS file

function App() {
  return (
    <div className="App container-fluid">
      {/* Header Section */}
      <header className="text-center py-5 bg-col hero-color text-white">
        <h1 className="display-4 mb-3">
          Welcome to UW Bus Baddies - Connecting Commuters!
        </h1>
        <p className="lead">Join us and find your commuting buddy.</p>
      </header>

      {/* Gallery Section */}
      <section className="mt-5">
        <h2 className="text-center mb-4">Recent Bus Baddies</h2>
        <Carousel autoPlay infiniteLoop={true} showThumbs={false} className="custom-carousel">
          {/* Carousel content here */}
        </Carousel>
      </section>

      <hr />

      <section className="mt-5">
        <h2 className="text-center mb-4">About Us</h2>
        {/* Add content for the "About Us" section */}
      </section>

      <section className="mt-5">
        <h2 className="text-center mb-4">Join Us</h2>
        {/* Add content for the "Join Us" section */}
        <Link to="/signin"> {/* Link to the sign-in page */}
          <button className="btn btn-primary">Sign In</button>
        </Link>
      </section>
    </div>
  );
}

export default App;
